import React from 'react';

class App extends React.Component {
  render(){
    const frutas = [
      "Perâ",
      "Banana",
      "Abacate",
      "Maça",
      "Abacaxi"
    ]
    return ( /*ale relembrar que o React retorna apénas 1 COMPONENTE sendo assim se quisermos retornar mais
      devemos envolver esse retorno em alguma coisa, como um objeto, um Array, uma div etc. */
      <div>  
        <h1> LISTA DA FEIRA</h1>
        <ul>
            { // Lembrnado que para chamarmos um código JS ou váriavel dentro do React usamos {}
              frutas.map((fruta, indice) => <li key ={indice}> {fruta}</li>) // Perceba como chamamos a função map DENTRO DO NOSSO REACT
            /* EXPLICANDO A KEY 
            O React, quando trabalha com array ele pede uma chave (key) para identificar cada um dos elementos que ele
            esta renderizando. Essa (key) deve ser única. 
            Esse pedido do React é interno apénas para quando for converter no navegador, tanto que ele nem aponta erro no Visual Studio
            mas se formos inspencionar o elemento no navegador vamos ver a mensagem de erro.  */


            }
        </ul>
      </div>
    );
  }
}

export default App; // Temos que exportar nossa classe para torná-la visível no nosso index
